; *****************************************************************************
;
;							Get Character => A, 0 if queue empty
;
; *****************************************************************************

XTGetKey:
		pshx
		pshy
		jsr 	$FFE4
		sta 	tempShort
		puly
		pulx
		lda 	tempShort
		rts

; *****************************************************************************
;
;								Update Clock Ticks
;
; *****************************************************************************

XTUpdateClock:
		pha
		pshx
		pshy
		jsr 	$FFDE
		sty 	ClockTicks+2
		stx 	ClockTicks+1
		sta 	ClockTicks
		lda 	#0
		sta 	ClockTicks+3
		puly
		pulx
		pla
		rts
