; *****************************************************************************
;
;			Print character A as text (for CX16 case issue PETSCII)
;
; *****************************************************************************

XTPrintAC:
		cmp 	#"a"
		bcc 	XTPrintA
		cmp 	#"z"+1
		bcs 	XTPrintA
		pha
		eor 	#$20
		jsr 	XTPrintA
		pla
		rts
		
; *****************************************************************************
;
;								Print CR/LF
;
; *****************************************************************************

XTPrintCR:		
		pha
		lda 	#13
		jsr 	XTPrintA
		pla
		rts

; *****************************************************************************
;
;								Print TAB (space will do)
;
; *****************************************************************************

XTPrintTab:		
		pha
		lda 	#32
		jsr 	XTPrintA
		pla
		rts			
; *****************************************************************************
;
;								Print character A
;
; *****************************************************************************

XTPrintA:
		sta 	tempShort
		pha
		pshx
		pshy
		lda 	tempShort
		jsr 	$FFD2
		puly
		pulx
		pla
		rts
